func (c *Checker) getTypeListId(types *[]*Type) string {
	result := ""
	if types != nil {
		length := types.length
		i := 0
		for i < length {
			startId := types[i].id
			count := 1
			for i+count < length && types[i+count].id == startId+count {
				count++
			}
			if result.length != 0 {
				result += ","
			}
			result += startId
			if count > 1 {
				result += ":" + count
			}
			i += count
		}
	}
	return result
}

