				c.flowLoopKeys[c.flowLoopCount] = key
				c.flowLoopTypes[c.flowLoopCount] = antecedentTypes
				c.flowLoopCount++
				saveFlowTypeCache := c.flowTypeCache
				c.flowTypeCache = nil
				flowType = getTypeAtFlowNode(antecedent)
				c.flowTypeCache = saveFlowTypeCache
				c.flowLoopCount--
