			instance := c.getReturnTypeOfSignature(signature)
			if c.isTypeAny(instance) {
				return instance
			}
			propType := c.getTypeOfPropertyOfType(instance, forcedLookupLocation)
			if propType == nil {
				return
			}
			results.push(propType)
