	switch {
	case resolvedSymbol:
		type_ = tc.getTypeOfSymbol(resolvedSymbol)
	case isDirectExport:
		type_ = tc.getRegularTypeOfLiteralType(tc.checkExpressionCached(expression.right))
	default:
		type_ = tc.getWidenedLiteralType(tc.checkExpressionCached(expression.right))
	}
