// Allows finding nodes in the source file at a certain position in an efficient manner.
// The implementation takes advantage of the calling pattern it knows the parser will
// make in order to optimize finding nodes as quickly as possible.
type SyntaxCursor struct {
	/* TODO(TS-TO-GO) Node InterfaceDeclaration: export interface SyntaxCursor { currentNode(position: number): Node; } */
}

func createSyntaxCursor(sourceFile SourceFile) SyntaxCursor {
	var currentArray NodeArray[*ast.Node] = sourceFile.Statements
	currentArrayIndex := 0

	Debug.assert(currentArrayIndex < currentArray.length)
	current := currentArray[currentArrayIndex]
	lastQueriedPosition := InvalidPositionValue

	return SyntaxCursor{
		currentNode: func(position number) *ast.Node {
			// Only compute the current node if the position is different than the last time
			// we were asked.  The parser commonly asks for the node at the same position
			// twice.  Once to know if can read an appropriate list element at a certain point,
			// and then to actually read and consume the node.
			if position != lastQueriedPosition {
				// Much of the time the parser will need the very next node in the array that
				// we just returned a node from.So just simply check for that case and move
				// forward in the array instead of searching for the node again.
				if current && current.End == position && currentArrayIndex < (currentArray.length-1) {
					currentArrayIndex++
					current = currentArray[currentArrayIndex]
				}

				// If we don't have a node, or the node we have isn't in the right position,
				// then try to find a viable node at the position requested.
				if !current || current.Pos != position {
					findHighestListElementThatStartsAtPosition(position)
				}
			}

			// Cache this query so that we don't do any extra work if the parser calls back
			// into us.  Note: this is very common as the parser will make pairs of calls like
			// 'isListElement -> parseListElement'.  If we were unable to find a node when
			// called with 'isListElement', we don't want to redo the work when parseListElement
			// is called immediately after.
			lastQueriedPosition = position

			// Either we don'd have a node, or we have a node at the position being asked for.
			Debug.assert(!current || current.Pos == position)
			return current
		},
	}

	// Finds the highest element in the tree we can find that starts at the provided position.
	// The element must be a direct child of some node list in the tree.  This way after we
	// return it, we can easily return its next sibling in the list.
	findHighestListElementThatStartsAtPosition := func(position number) {
		// Clear out any cached state about the last node we found.
		currentArray = nil
		currentArrayIndex = InvalidPositionValue
		current = nil

		// Recurse into the source file to find the highest node at this position.
		forEachChild(sourceFile, visitNode, visitArray)
		return

		visitNode := func(node *ast.Node) bool {
			if position >= node.Pos && position < node.End {
				// Position was within this node.  Keep searching deeper to find the node.
				forEachChild(node, visitNode, visitArray)

				// don't proceed any further in the search.
				return true
			}

			// position wasn't in this node, have to keep searching.
			return false
		}

		visitArray := func(array NodeArray[*ast.Node]) bool {
			if position >= array.pos && position < array.end {
				// position was in this array.  Search through this array to see if we find a
				// viable element.
				for i := 0; i < array.length; i++ {
					child := array[i]
					if child {
						if child.Pos == position {
							// Found the right node.  We're done.
							currentArray = array
							currentArrayIndex = i
							current = child
							return true
						} else {
							if child.Pos < position && position < child.End {
								// Position in somewhere within this child.  Search in it and
								// stop searching in this array.
								forEachChild(child, visitNode, visitArray)
								return true
							}
						}
					}
				}
			}

			// position wasn't in this array, have to keep searching.
			return false
		}

	}

}

